mapper.select:


  	select * from (
  		select /*+INDEX_DESC(shop_author SYS_C007424) */ 
  			rownum as rn, shopid, shopname, nationid, updatedate, regdate
		from shop_author 
		where



			shopname like '%'||#{keyword}||'%' and



		 rownum <= #{pageNum}*#{amount}
		)
	where rn > (#{pageNum} - 1) * #{amount}
